CommandUIHandler.CommandAction:
javascript: (function() {
        var options = {
            url: _spPageContextInfo.webAbsoluteUrl + '/ProjectSitePages/SuspendLicense.aspx?ListId={ListId}&ItemId={ItemId}',
            title: 'Приостановка действия разрешения',
            allowMaximize: false,
            showClose: true,
            dialogReturnValueCallback: Function.createDelegate(null, function (result, returnValue) {
                if (result == SP.UI.DialogResult.OK) {
                    if (returnValue == null) {
                        TM.SP.showIconNotification('Действие разрешения приостановлено', '_layouts/15/images/kpinormal-0.gif', true);
                        setTimeout(function() {
                          var gobackBtn = $('input[type=button][name*="GoBack"]');
                          if (gobackBtn) {
                            gobackBtn.click();
                          }
                        }, 2000);
                    }
                    else {
                        location.href = returnValue;
                    }
                }
                else if (result == -1){
                    TM.SP.showIconNotification('В процессе приостановки разрешения возникли ошибки', '_layouts/15/images/kpinormal-2.gif', true);
                    setTimeout(function() {
                      SP.UI.ModalDialog.RefreshPage(SP.UI.DialogResult.cancel);  
                    }, 2000);
                }
            })
        };

        SP.UI.ModalDialog.showModalDialog(options);

      })();

CommandUIHandler.EnabledScript:
javascript: function enabled() { 
            var result = false;

            if (TM && TM.SP_ && TM.SP_.License && TM.SP_.License.getCurrent && TM.SP_.License.getCurrent()) {
              var license = TM.SP_.License.getCurrent();
              var isLast = license.get_item('_x0421__x0441__x044b__x043b__x04');
              var status = license.get_item('Tm_LicenseStatus');

              result = isLast && status && status != 'Аннулировано' && status != 'Приостановлено';
            }
            else {
              SP.SOD.executeOrDelayUntilScriptLoaded(function() {
                RefreshCommandUI();
              }, 'CurrentLicense.js');
            }

            return result;
          } enabled();
